  constructor() {
    super();
    this.state = { name: "Ram yadav" };
  }
  render() {
    return (
      <div className="container text-center">
        <p onClick={()=>{this.setState({name:'krsihna'})}}>{this.state.name}</p>
        <User name={this.state.name}/>
        <User1 name={this.state.name}/>
      </div>
    );
  }
}
